	private void sendEmail(String to, String html, String subject) {
	    try {
	    	BasicAWSCredentials awsCreds = new BasicAWSCredentials(configurations.AWS_SES_KEY_ID, configurations.AWS_SES_SECRET_KEY);
	    	
	    	AmazonSimpleEmailService client = AmazonSimpleEmailServiceClientBuilder.standard()
	            .withRegion(Regions.EU_WEST_1)
	            .withCredentials(new AWSStaticCredentialsProvider(awsCreds))
	            .build();
	    	
	    	SendEmailRequest request = new SendEmailRequest().withDestination(
	    			new Destination().withToAddresses(to))
	    			.withMessage(new Message()
	    			.withBody(new Body()
	    			.withHtml(new Content()
	                .withCharset("UTF-8").withData(html))
	                .withText(new Content()
	                .withCharset("UTF-8").withData(TEXTBODY)))
	    			.withSubject(new Content()
	                .withCharset("UTF-8").withData(subject)))
	    			.withSource(FROM);
	    	client.sendEmail(request);
	    } catch (Exception e) {e.printStackTrace();}
	}
